class ShutterstockAPI {
  constructor() { }

  getAudio(mood) {
    let url = `https://api.shutterstock.com/v2/audio/search`;
    let token = ``;
    // token은 J112 개인키입니다.

    return fetch(`${url}?moods=${mood}`, {
      method: "GET",
      headers: { 'Authorization': `Bearer ${token}` }
    })
  }
}
